mapper.select:
SELECT CH.CHATROOM_NO,CH.CHATROOM_ADMIN_NO,CH.CHATROOM_MEMBER_NO,M.MEMBER_NAME, M.MEMBER_PROFILE_URL
FROM CLASS C INNER JOIN MEMBER A ON (C.ADMIN_NO = A.MEMBER_NO) INNER JOIN ENROLL E ON (C.CLASS_NO = E.CLASS_NO) INNER JOIN MEMBER M ON (E.MEMBER_NO = M.MEMBER_NO) INNER JOIN CHATROOM CH ON (M.MEMBER_NO = CH.CHATROOM_MEMBER_NO AND A.MEMBER_NO = CH.CHATROOM_ADMIN_NO) WHERE CH.CHATROOM_ADMIN_NO = #{loginMember.memberNo}
AND C.CLASS_NO = #{cri.classNo}
AND M.MEMBER_NAME like CONCAT('%', #{cri.memberName}, '%')
FROM CHATROOM CH JOIN MEMBER M ON(CH.CHATROOM_MEMBER_NO = M.MEMBER_NO) WHERE CH.CHATROOM_ADMIN_NO = #{loginMember.memberNo}
FROM CHATROOM CH JOIN MEMBER M ON(CH.CHATROOM_ADMIN_NO = M.MEMBER_NO) WHERE CH.CHATROOM_MEMBER_NO = #{loginMember.memberNo}
AND CH.CHATROOM_STATUS = 'Y' ORDER BY CH.CHATROOM_NO DESC

mapper.update:
		UPDATE CHATROOM
      SET CHATROOM_STATUS = 'N'
      WHERE CHATROOM_NO = #{chatRoomNo}

mapper.select:
SELECT COUNT(*)
FROM CHATROOM
WHERE CHATROOM_MEMBER_NO = #{memberNo}  AND CHATROOM_ADMIN_NO = #{adminNo}

mapper.insert:
INSERT INTO CHATROOM(
	CHATROOM_ADMIN_NO,
	CHATROOM_MEMBER_NO
)
VALUES(
	#{adminNo},
	#{memberNo}
)

mapper.select:
SELECT CHATROOM_ADMIN_NO,CHATROOM_MEMBER_NO
FROM CHATROOM
WHERE CHATROOM_NO=#{chatRoomNo}
